import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import './Mapmanage.css';
import { faEdit, faPlus } from '@fortawesome/free-solid-svg-icons';

function Mapmanage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null); // 선택된 맵 상태
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false); // 업로드 팝업 상태
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false); // 수정 팝업 상태
  const [isCreatingNode, setIsCreatingNode] = useState(false); // 노드 생성 모드
  const [nodeX, setNodeX] = useState(null); // X 좌표
  const [nodeY, setNodeY] = useState(null); // Y 좌표
  const [nodeName, setNodeName] = useState(''); // 노드 이름
  const [isNodePopupOpen, setIsNodePopupOpen] = useState(false); // 노드 팝업 상태
  const mapRef = React.useRef(null); // 지도 영역 참조


  useEffect(() => {
    fetchMaps();
  }, []);

  const fetchMaps = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.15:5557/map/maps', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
    }
  };

  const handleSelectMap = async (map) => {
    setSelectedMap(map); // 맵 선택 상태 업데이트
    setEditName(map.name);
    setEditDescription(map.description);

    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.15:5557/map/file/${map._id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl);
    } catch (error) {
      console.error('Error fetching map image:', error);
    }
  };

  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    const confirmDelete = window.confirm('Are you sure you want to delete this map?');
    if (!confirmDelete) return;

    try {
      await axios.put(`http://172.30.1.15:5557/map/delete/${mapId}`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Map deleted successfully');
      fetchMaps();
      if (selectedMap && selectedMap._id === mapId) {
        setSelectedMap(null); // 선택된 맵을 삭제한 경우 초기화
      }
    } catch (error) {
      console.error('Error deleting map:', error);
      alert('Error deleting map');
    }
  };

  const handleFileChange = (e) => {
    setMapFile(e.target.files[0]);
  };

  const handleYamlFileChange = (e) => {
    setYamlFile(e.target.files[0]);
  };

  const handleUpdateMap = async (e) => {
    e.preventDefault();

    if (editName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.put(`http://172.30.1.15:5557/map/update/${selectedMap._id}`, {
        name: editName,
        description: editDescription,
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Map updated successfully');
      setIsEditPopupOpen(false); // 수정 후 팝업 닫기
      fetchMaps();
    } catch (error) {
      console.error('Error updating map:', error);
    }
  };

  const handleUploadMap = async (e) => {
    e.preventDefault();

    if (uploadName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }

    const formData = new FormData();
    formData.append('name', uploadName);
    formData.append('description', uploadDescription);
    formData.append('file', mapFile);
    formData.append('yaml', yamlFile);

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.post('http://172.30.1.15:5557/map/upload', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Map uploaded successfully');
      setIsUploadPopupOpen(false); // 업로드 후 팝업 닫기
      fetchMaps();
    } catch (error) {
      console.error('Error uploading map:', error);
    }
  };

  const openEditPopup = (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);
    setIsEditPopupOpen(true); // 수정 팝업 열기
  };
  const handleCreateNode = () => {
    if (!selectedMap) {
      alert('Please select a map to create a node.');
      return;
    }
    alert(`Creating a new node for map: ${selectedMap.name}`);
  };
  // 노드 생성 모드 시작
const handleCreateNodeMode = () => {
  if (!selectedMap) {
    alert('Please select a map first.');
    return;
  }
  setIsCreatingNode(true);
  document.body.style.cursor = 'crosshair'; // 마우스 커서 십자가 모양 변경
};

// 지도 클릭 시 좌표를 얻고 팝업 열기
const handleMapClick = (e) => {
  if (!isCreatingNode) return;

  const rect = mapRef.current.getBoundingClientRect();
  const x = e.clientX - rect.left; // 클릭한 X 좌표 계산
  const y = e.clientY - rect.top;  // 클릭한 Y 좌표 계산

  setNodeX(x);
  setNodeY(y);
  setIsCreatingNode(false);
  setIsNodePopupOpen(true); // 팝업 열기
  document.body.style.cursor = 'default'; // 커서 복구
};
const handleSubmitNode = async () => {
  const token = localStorage.getItem('token');
  if (!token) {
    alert('No token found, please log in again.');
    return;
  }

  try {
    await axios.post('http://172.30.1.15:5557/map/nodes', {
      name: nodeName,
      x: nodeX,
      y: nodeY,
      mapId: selectedMap._id,
    }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    alert('Node created successfully.');
    setIsNodePopupOpen(false);
    setNodeName(''); // 입력 필드 초기화
  } catch (error) {
    console.error('Error creating node:', error);
    alert('Failed to create node.');
  }
};

return (
  <div className="map-management-page">
    {/* 상단 네비게이션 바 */}
    <header className="map-management-header">
      <UserInfo />
      <LogoutButton />
    </header>
    <Navbar />

    {/* 툴바: 네비게이션 아래에 위치 */}
    <div className="toolbar">
      <button onClick={handleCreateNodeMode} className="create-node-button">
        <FontAwesomeIcon icon={faPlus} /> Create Node
      </button>
    </div>

    {/* 맵 리스트와 이미지 컨텐츠 */}
    <div className="map-management-content">
      {/* 맵 리스트 컨테이너 */}
      <div className="map-list-container">
        <h2 className="map-list-header">
          Map List
          <button
            onClick={() => setIsUploadPopupOpen(true)}
            className="add-map-button"
          >
            +
          </button>
        </h2>
        <ul className="map-list">
          {maps.map((map) => (
            <li
              key={map._id}
              className={`map-list-item ${
                selectedMap && selectedMap._id === map._id ? 'selected-map' : ''
              }`}
            >
              <span
                onClick={() => handleSelectMap(map)}
                className="map-list-item-name"
              >
                {map.name}
              </span>
              <button
                onClick={() => openEditPopup(map)}
                className="edit-map-button"
              >
                <FontAwesomeIcon icon={faEdit} />
              </button>
              <button
                onClick={() => handleDeleteMap(map._id)}
                className="delete-map-button"
              >
                &times;
              </button>
            </li>
          ))}
        </ul>

        {selectedMap && (
          <div className="map-description">
            <h3>Map Description</h3>
            <p>{selectedMap.description}</p>
          </div>
        )}
      </div>

      {/* 이미지 영역 */}
      <div
        ref={mapRef}
        className="map-image-container"
        onClick={handleMapClick} // 클릭 이벤트 추가
        style={{ width: '800px', height: '600px', backgroundColor: '#e0e0e0' }}
      >
        {mapImage ? (
          <img src={mapImage} alt="Selected Map" className="map-image" />
        ) : (
          <p>Select a map to view its image</p>
        )}
      </div>
    </div>

    {/* Node 생성 팝업 */}
    {isNodePopupOpen && (
      <div className="popup-container">
        <h3>Create Node</h3>
        <p>X: {nodeX}, Y: {nodeY}</p>
        <input
          type="text"
          value={nodeName}
          onChange={(e) => setNodeName(e.target.value)}
          placeholder="Enter node name"
          className="popup-input"
        />
        <div className="popup-button-container">
          <button onClick={handleSubmitNode} className="popup-submit-button">
            Create
          </button>
          <button
            onClick={() => setIsNodePopupOpen(false)}
            className="popup-cancel-button"
          >
            Cancel
          </button>
        </div>
      </div>
    )}

    {/* Map Upload Popup */}
    {isUploadPopupOpen && (
      <div className="popup-container">
        <h3>Upload Map</h3>
        <form onSubmit={handleUploadMap}>
          <input
            type="text"
            value={uploadName}
            onChange={(e) => setUploadName(e.target.value)}
            placeholder="Map Name"
            required
            className="popup-input"
          />
          <textarea
            value={uploadDescription}
            onChange={(e) => setUploadDescription(e.target.value)}
            placeholder="Map Description"
            required
            className="popup-textarea"
          />
          <label>Map File (.pgm, .png, .jpg)</label>
          <input
            type="file"
            accept=".pgm,.png,.jpg"
            onChange={handleFileChange}
            required
            className="popup-file-input"
          />
          <label>YAML File (.yaml)</label>
          <input
            type="file"
            accept=".yaml"
            onChange={handleYamlFileChange}
            required
            className="popup-file-input"
          />
          <div className="popup-button-container">
            <button type="submit" className="popup-submit-button">
              Upload
            </button>
            <button
              type="button"
              onClick={() => setIsUploadPopupOpen(false)}
              className="popup-cancel-button"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    )}

    {/* Edit Map Popup */}
    {isEditPopupOpen && (
      <div className="popup-container">
        <h3>Edit Map</h3>
        <form onSubmit={handleUpdateMap}>
          <input
            type="text"
            value={editName}
            onChange={(e) => setEditName(e.target.value)}
            placeholder="Map Name"
            required
            className="popup-input"
          />
          <textarea
            value={editDescription}
            onChange={(e) => setEditDescription(e.target.value)}
            placeholder="Map Description"
            required
            className="popup-textarea"
          />
          <div className="popup-button-container">
            <button type="submit" className="popup-submit-button">
              Update
            </button>
            <button
              type="button"
              onClick={() => setIsEditPopupOpen(false)}
              className="popup-cancel-button"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    )}
  </div>
);

}

export default Mapmanage;
