import React, { useState, useEffect } from 'react';
import './NodeList.css'; // 스타일 파일 추가
import { FaChevronUp, FaChevronDown, FaPlus } from 'react-icons/fa';
import axios from 'axios'; // API 요청을 위한 axios

function NodeList({ nodes }) {
  const [expandedNodeIds, setExpandedNodeIds] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // 모달 상태
  const [stepList, setStepList] = useState([]); // StepList 데이터를 저장할 상태
  const [selectedStep, setSelectedStep] = useState(''); // 선택된 Step을 저장할 상태
  const [token, setToken] = useState(''); // Bearer 토큰을 저장할 상태

  // 컴포넌트가 처음 렌더링될 때 토큰을 로컬 스토리지에서 가져옴
  useEffect(() => {
    const storedToken = localStorage.getItem('token'); // 로컬 스토리지에서 토큰 가져오기
    if (storedToken) {
      setToken(storedToken);
    } else {
      console.error('No token found');
    }
  }, []);

  // 노드를 클릭할 때 펼치거나 접는 함수
  const toggleNodeDetails = (nodeId) => {
    if (expandedNodeIds.includes(nodeId)) {
      setExpandedNodeIds(expandedNodeIds.filter(id => id !== nodeId));
    } else {
      setExpandedNodeIds([...expandedNodeIds, nodeId]);
    }
  };

  // StepList 데이터를 가져오는 함수
  const fetchStepList = async () => {
    try {
      const response = await axios.get('http://172.30.1.15:5557/map/steps', {
        headers: {
          'Authorization': `Bearer ${token}` // Bearer 토큰을 헤더에 추가
        }
      });
      setStepList(response.data.steps || []); // Step 데이터를 상태에 저장, 배열이 아닐 경우 기본 빈 배열 설정
    } catch (error) {
      console.error('Error fetching step list:', error);
      setStepList([]); // 에러가 발생할 경우 빈 배열 설정
    }
  };

  // Step 추가 버튼 클릭 시 모달을 열고 StepList 데이터를 불러옴
  const handleAddStep = () => {
    fetchStepList(); // StepList 데이터를 불러옴
    setIsModalOpen(true); // 모달 열기
  };

  // 모달 닫기 함수
  const handleCloseModal = () => {
    setIsModalOpen(false); // 모달 닫기
  };

  // 선택된 Step 변경 함수
  const handleStepChange = (e) => {
    setSelectedStep(e.target.value); // 선택된 Step 상태 업데이트
  };

  return (
    <div className="node-list-container">
      <h3>Nodes List</h3>
      {nodes.length > 0 ? (
        <ul>
          {nodes.map((node) => (
            <li key={node._id} className="node-list-item">
              <div className="node-header" onClick={() => toggleNodeDetails(node._id)}>
                <strong>{node.name}</strong>: ({node.x.toFixed(2)}, {node.y.toFixed(2)})
                {expandedNodeIds.includes(node._id) ? <FaChevronUp /> : <FaChevronDown />}
              </div>

              <div className={`node-details-wrapper ${expandedNodeIds.includes(node._id) ? 'expanded' : 'collapsed'}`}>
                <div className="node-details">
                  <p><strong>Node ID:</strong> {node._id}</p>
                  <p><strong>Coordinates:</strong> ({node.x.toFixed(2)}, {node.y.toFixed(2)})</p>

                  <button className="add-step-button" onClick={handleAddStep}>
                    <FaPlus /> <span className="add-step-text">Step 추가</span>
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No nodes available for this map.</p>
      )}

      {/* 모달 */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Step 추가</h2>
            {/* 드롭다운 리스트에서 Step 선택 */}
            <label htmlFor="stepSelect">Step을 선택하세요:</label>
            <select id="stepSelect" value={selectedStep} onChange={handleStepChange}>
              <option value="">Step을 선택하세요</option>
              {stepList.length > 0 && stepList.map((step) => (
                <option key={step._id} value={step._id}>
                  {step.name}
                </option>
              ))}
            </select>

            <div className="modal-actions">
              <button onClick={() => alert(`선택된 Step: ${selectedStep}`)}>추가</button>
              <button onClick={handleCloseModal}>닫기</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default NodeList;
