import React, { useState, useEffect } from 'react';
import './NodeList.css'; // 스타일 파일 추가
import { FaChevronUp, FaChevronDown, FaPlus } from 'react-icons/fa'; // 아이콘 임포트
import axios from 'axios'; // API 호출을 위해 axios 임포트

function NodeList({ nodes }) {
  const [expandedNodeIds, setExpandedNodeIds] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // 모달 상태
  const [stepList, setStepList] = useState([]); // 불러온 Step 목록 상태
  const [selectedStep, setSelectedStep] = useState(null); // 선택된 Step
  const [currentNodeId, setCurrentNodeId] = useState(null); // 현재 Step을 추가할 노드 ID

  // 노드를 클릭할 때 펼치거나 접는 함수
  const toggleNodeDetails = (nodeId) => {
    if (expandedNodeIds.includes(nodeId)) {
      setExpandedNodeIds(expandedNodeIds.filter(id => id !== nodeId));
    } else {
      setExpandedNodeIds([...expandedNodeIds, nodeId]);
    }
  };

  // 특정 노드의 Step을 가져오는 함수
  const fetchSteps = async () => {
    try {
      const response = await axios.get('http://your-api-url/steps'); // stepList 가져오기
      setStepList(response.data.steps); // 가져온 step 데이터를 상태에 저장
    } catch (error) {
      console.error('Error fetching steps:', error);
    }
  };

  // Step 추가 버튼 클릭 시 동작할 함수
  const handleAddStepClick = (nodeId) => {
    setCurrentNodeId(nodeId); // 현재 Step을 추가할 노드 설정
    fetchSteps(); // Step 목록을 가져옴
    setIsModalOpen(true); // 모달 열기
  };

  // 모달에서 Step 선택
  const handleStepSelect = (step) => {
    setSelectedStep(step); // 선택된 Step 상태 저장
  };

  // Step 추가 처리 함수
  const handleStepAdd = () => {
    if (selectedStep && currentNodeId) {
      alert(`Node ${currentNodeId}에 Step "${selectedStep.name}" 추가`);
      // 여기서 선택된 Step을 해당 Node에 추가하는 API 호출을 할 수 있음
      // axios.post('http://your-api-url/nodes/addStep', { nodeId: currentNodeId, stepId: selectedStep._id });
    }
    setIsModalOpen(false); // 모달 닫기
    setSelectedStep(null); // 선택된 Step 초기화
    setCurrentNodeId(null); // 현재 노드 초기화
  };

  return (
    <div className="node-list-container">
      <h3>Nodes List</h3>
      {nodes.length > 0 ? (
        <ul>
          {nodes.map((node) => (
            <li key={node._id} className="node-list-item">
              <div className="node-header" onClick={() => toggleNodeDetails(node._id)}>
                <strong>{node.name}</strong>: ({node.x.toFixed(2)}, {node.y.toFixed(2)})
                {expandedNodeIds.includes(node._id) ? <FaChevronUp /> : <FaChevronDown />}
              </div>

              <div className={`node-details-wrapper ${expandedNodeIds.includes(node._id) ? 'expanded' : 'collapsed'}`}>
                <div className="node-details">
                  <p><strong>Node ID:</strong> {node._id}</p>
                  <p><strong>Coordinates:</strong> ({node.x.toFixed(2)}, {node.y.toFixed(2)})</p>

                  <button className="add-step-button" onClick={() => handleAddStepClick(node._id)}>
                    <FaPlus /> <span className="add-step-text">Step 추가</span>
                  </button>
                </div>
              </div>

              {/* Step 선택 모달 */}
              {isModalOpen && (
                <div className="modal">
                  <div className="modal-content">
                    <h4>Step 선택</h4>
                    <div className="step-list-scroll">
                      <ul>
                        {stepList.map((step) => (
                          <li
                            key={step._id}
                            className={`step-item ${selectedStep?._id === step._id ? 'selected' : ''}`}
                            onClick={() => handleStepSelect(step)}
                          >
                            {step.name}
                          </li>
                        ))}
                      </ul>
                    </div>
                    <div className="modal-actions">
                      <button onClick={handleStepAdd} disabled={!selectedStep}>추가</button>
                      <button onClick={() => setIsModalOpen(false)}>취소</button>
                    </div>
                  </div>
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No nodes available for this map.</p>
      )}
    </div>
  );
}

export default NodeList;
