import React, { useState, useEffect, useRef, useCallback } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import io from 'socket.io-client';

// WebRTC 신호 서버에 연결 (라즈베리파이)
const webcamSocket = io('http://172.30.1.76:7000', {
    transports: ['websocket'],
    withCredentials: true
});

webcamSocket.on('connect', () => {
    console.log('Webcam socket connected');
});

webcamSocket.on('disconnect', () => {
    console.log('Webcam socket disconnected');
});

webcamSocket.on('connect_error', (error) => {
    console.error('Webcam socket connection error:', error);
});

webcamSocket.on('error', (error) => {
    console.error('Webcam socket error:', error);
});

// 원격 PC의 SLAM 데이터 수신을 위한 WebSocket 연결
const slamSocket = io('http://172.30.1.40:3000', {
    transports: ['websocket'],
    withCredentials: true
});

slamSocket.on('connect', () => {
    console.log('SLAM socket connected');
});

slamSocket.on('disconnect', () => {
    console.log('SLAM socket disconnected');
});

slamSocket.on('connect_error', (error) => {
    console.error('SLAM socket connection error:', error);
});

slamSocket.on('error', (error) => {
    console.error('SLAM socket error:', error);
});

function MapCreatePage() {
    const [robots, setRobots] = useState([]);
    const [selectedRobot, setSelectedRobot] = useState('');
    const videoRef = useRef();
    const peerRef = useRef(null);
    const mapRef = useRef(null);

    // 로봇 목록을 가져오는 부분
    useEffect(() => {
        const fetchRobots = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://172.30.1.40:5559/robot/robots', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setRobots(response.data);
            } catch (error) {
                console.error('Error fetching robots:', error);
            }
        };

        fetchRobots();

        return () => {
            webcamSocket.disconnect();
            slamSocket.disconnect();
        };
    }, []);

    // 선택된 로봇의 웹캠 스트림을 수신하는 부분
    useEffect(() => {
        if (selectedRobot) {
            const peer = new RTCPeerConnection();

            peer.ontrack = (event) => {
                console.log('Stream received:', event.streams[0]);
                if (videoRef.current) {
                    videoRef.current.srcObject = event.streams[0];
                }
            };

            peer.onicecandidate = (event) => {
                if (event.candidate) {
                    console.log('Sending candidate:', event.candidate);
                    webcamSocket.emit('candidate', event.candidate);
                }
            };

            peer.oniceconnectionstatechange = () => {
                console.log('ICE Connection State Change:', peer.iceConnectionState);
            };

            peer.onconnectionstatechange = () => {
                console.log('Connection State Change:', peer.connectionState);
            };

            peerRef.current = peer;

            // 신호 데이터 전송
            console.log('Joining with robot_id:', selectedRobot);
            webcamSocket.emit('join', { robot_id: selectedRobot });

            webcamSocket.on('signal', async (data) => {
                console.log('Received signal:', data);
                const { signal } = data;
                try {
                    if (signal.type === 'offer') {
                        await peer.setRemoteDescription(new RTCSessionDescription(signal));
                        const answer = await peer.createAnswer();
                        await peer.setLocalDescription(answer);
                        webcamSocket.emit('signal', { signal: peer.localDescription, robot_id: selectedRobot });
                    } else if (signal.type === 'answer') {
                        await peer.setRemoteDescription(new RTCSessionDescription(signal));
                    }
                } catch (error) {
                    console.error('Error handling signal:', error);
                }
            });

            webcamSocket.on('candidate', async (candidate) => {
                console.log('Received candidate:', candidate);
                try {
                    await peer.addIceCandidate(new RTCIceCandidate(candidate));
                } catch (error) {
                    console.error('Error adding ICE candidate:', error);
                }
            });

            return () => {
                if (peerRef.current) {
                    peerRef.current.close();
                }
            };
        }
    }, [selectedRobot]);

    // ROS 연결 설정 및 SLAM 데이터 처리 부분
    useEffect(() => {
        if (selectedRobot) {
            const ros = new window.ROSLIB.Ros({
                url: 'ws://172.30.1.40:9090'
            });

            ros.on('connection', () => {
                console.log('Connected to ROS websocket server.');
            });

            ros.on('error', (error) => {
                console.log('Error connecting to ROS websocket server: ', error);
            });

            ros.on('close', () => {
                console.log('Connection to ROS websocket server closed.');
            });

            const mapTopic = new window.ROSLIB.Topic({
                ros: ros,
                name: '/map',
                messageType: 'nav_msgs/OccupancyGrid'
            });

            mapTopic.subscribe((message) => {
                console.log('Received message on /map: ', message);
                if (message.info && message.data) {
                    const { width, height } = message.info;
                    const data = message.data;
                    const canvas = mapRef.current;
                    const context = canvas.getContext('2d');
                    const imageData = context.createImageData(width, height);

                    for (let i = 0; i < data.length; i++) {
                        const value = data[i];
                        const color = value === -1 ? 255 : 255 - value;
                        imageData.data[i * 4] = color;
                        imageData.data[i * 4 + 1] = color;
                        imageData.data[i * 4 + 2] = color;
                        imageData.data[i * 4 + 3] = 255;
                    }

                    context.putImageData(imageData, 0, 0);
                } else {
                    console.error('Received invalid /map message:', message);
                }
            });

            return () => {
                ros.close();
            };
        }
    }, [selectedRobot]);

    // 키보드 입력으로 로봇을 제어하는 부분
    const handleKeyDown = useCallback((e) => {
        const velocityCommands = {
            w: { linear: 0.1, angular: 0 },
            a: { linear: 0, angular: 0.1 },
            s: { linear: -0.1, angular: 0 },
            d: { linear: 0, angular: -0.1 },
            ' ': { linear: 0, angular: 0 }
        };
        if (velocityCommands[e.key]) {
            slamSocket.emit('key_press', { robot_id: selectedRobot, velocity: velocityCommands[e.key] });
        }
    }, [selectedRobot]);

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        return () => {
            window.removeEventListener('keydown', handleKeyDown);
        };
    }, [selectedRobot, handleKeyDown]);

    return (
        <div>
            <header>
                <UserInfo />
                <LogoutButton />
            </header>
            <div style={{ display: 'flex' }}>
                <Navbar />
            </div>
            <h2>SLAM Control</h2>
            <div>
                <label>로봇 선택:</label>
                <select onChange={(e) => setSelectedRobot(e.target.value)} value={selectedRobot}>
                    <option value="">로봇을 선택하세요</option>
                    {robots.map(robot => (
                        <option key={robot._id} value={robot._id}>{robot.name}</option>
                    ))}
                </select>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <div style={{ width: '50%' }}>
                    <h3>SLAM 화면</h3>
                    <canvas ref={mapRef} width="600" height="600" />
                </div>
                <div style={{ width: '50%' }}>
                    <h3>WebCam 화면</h3>
                    <video ref={videoRef} autoPlay playsInline style={{ width: '100%' }} />
                </div>
            </div>
        </div>
    );
}

export default MapCreatePage;
