import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import CreateTask from './CreateTask'; // CreateTask 컴포넌트 임포트
import TaskFlow from './TaskFlow'; // TaskFlow 컴포넌트 임포트
import './TaskPage.css'; // CSS 파일을 임포트

function TaskPage() {
    const [tasks, setTasks] = useState([]);
    const [selectedTask, setSelectedTask] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [monitoredMap, setMonitoredMap] = useState(null);
    const [temporaryWorkflow, setTemporaryWorkflow] = useState([]); // 임시 작업 플로우 상태

    const [taskName, setTaskName] = useState('');
    const [taskDescription, setTaskDescription] = useState('');
    const [taskType, setTaskType] = useState('A');

    const token = localStorage.getItem('token');

    useEffect(() => {
        async function fetchTasks() {
            try {
                const mapResponse = await axios.get('http://172.30.1.30:5557/map/monitored', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const monitoredMap = mapResponse.data;
                setMonitoredMap(monitoredMap);

                if (!monitoredMap || !monitoredMap._id) {
                    console.error('모니터링 중인 맵 정보가 없습니다.');
                    return;
                }

                const taskResponse = await axios.get(`http://172.30.1.30:8080/task/tasks?mapId=${monitoredMap._id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setTasks(taskResponse.data);

                if (taskResponse.data.length > 0) {
                    setSelectedTask(taskResponse.data[0]);
                    setTaskName(taskResponse.data[0].name);
                    setTaskDescription(taskResponse.data[0].description || '');
                    setTaskType(taskResponse.data[0].taskType || 'A');
                }
            } catch (error) {
                console.error('작업 리스트를 가져오는 중 오류가 발생했습니다.', error);
            }
        }
        fetchTasks();
    }, [token]);

    const handleTaskClick = (task) => {
        setSelectedTask(task);
        setTaskName(task.name);
        setTaskDescription(task.description || '');
        setTaskType(task.taskType || 'A');
        setTemporaryWorkflow([]); // 새로운 작업 선택 시 임시 플로우 초기화
    };

    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    };

    const handleTaskCreated = (newTask) => {
        setTasks([...tasks, newTask]); // 작업 목록에 추가
        setTemporaryWorkflow([...temporaryWorkflow, { node: newTask.name, step: newTask.description }]); // 임시 플로우에 추가
        togglePopup();
    };

    const handleSaveTask = async () => {
        if (selectedTask) {
            try {
                const updatedTask = {
                    ...selectedTask,
                    workflow: [...selectedTask.workflow, ...temporaryWorkflow], // 기존 워크플로우와 임시 플로우 병합
                    name: taskName,
                    description: taskDescription,
                    taskType: taskType,
                };

                const response = await axios.put(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}`, updatedTask, {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setSelectedTask(response.data);
                setTemporaryWorkflow([]); // 저장 후 임시 플로우 초기화
                alert('작업이 성공적으로 저장되었습니다.');
            } catch (error) {
                console.error('작업 저장 중 오류가 발생했습니다.', error);
                alert('작업 저장 중 오류가 발생했습니다.');
            }
        } else {
            alert('저장할 작업을 선택하세요.');
        }
    };

    const handleDeleteTask = async () => {
        if (selectedTask) {
            try {
                const response = await axios.put(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}`, {
                    name: '0',
                }, {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setTasks(tasks.filter(task => task._id !== selectedTask._id));
                setSelectedTask(null);
                alert('작업이 삭제되었습니다.');
            } catch (error) {
                console.error('작업 삭제 중 오류가 발생했습니다.', error);
                alert('작업 삭제 중 오류가 발생했습니다.');
            }
        } else {
            alert('삭제할 작업을 선택하세요.');
        }
    };

    return (
        <div className="page-container">
            <header className="header">
                <UserInfo />
                <LogoutButton />
            </header>
            <Navbar className="navbar" />
            <div className="main-content">
                <div className="task-list">
                    <div className="task-list-header">
                        <h3>
                            작업 리스트
                            {monitoredMap && (
                                <span style={{ color: 'gray', marginLeft: '10px', fontSize: '14px' }}>
                                    - {monitoredMap.name}
                                </span>
                            )}
                        </h3>
                        <button className="add-task-btn" onClick={togglePopup}>+</button>
                    </div>
                    <ul>
                        {tasks.map((task) => (
                            <li
                                key={task._id}
                                onClick={() => handleTaskClick(task)}
                                className={selectedTask && selectedTask._id === task._id ? 'selected' : ''}
                            >
                                {task.name}
                            </li>
                        ))}
                    </ul>
                </div>

                <div className="task-details">
                    {selectedTask ? (
                        <div>
                            <h3>작업 상세 정보</h3>
                            <label>
                                <strong>작업 이름:</strong>
                                <input
                                    type="text"
                                    className="input-field"
                                    value={taskName}
                                    onChange={(e) => setTaskName(e.target.value)}
                                />
                            </label>
                            <br />
                            <label>
                                <strong>작업 설명:</strong>
                                <textarea
                                    className="textarea-field"
                                    value={taskDescription}
                                    onChange={(e) => setTaskDescription(e.target.value)}
                                />
                            </label>
                            <br />
                            <div className="task-row">
                                <label>
                                    <strong>작업 유형:</strong>
                                    <select
                                        className="select-field"
                                        value={taskType}
                                        onChange={(e) => setTaskType(e.target.value)}
                                    >
                                        <option value="A">A</option>
                                        <option value="B">B</option>
                                        <option value="C">C</option>
                                    </select>
                                </label>
                                <label className="created-date">
                                    <strong>생성일:</strong> {new Date(selectedTask.createdAt).toLocaleDateString()}
                                </label>
                            </div>

                            {/* 작업 플로우 */}
                            <h4>작업 플로우</h4>
                            <div className="task-flow-section">
                                <TaskFlow workflow={[...selectedTask.workflow, ...temporaryWorkflow]} />
                            </div>
                        </div>
                    ) : (
                        <p>작업을 선택하세요.</p>
                    )}
                </div>

                {isPopupOpen && (
                    <CreateTask onClose={togglePopup} onTaskCreated={handleTaskCreated} />
                )}

                <div className="floating-buttons">
                    <button className="assign-task-btn" onClick={handleAssignTask}>작업 할당</button>
                    <button className="save-task-btn" onClick={handleSaveTask}>작업 저장</button>
                    <button className="delete-task-btn" onClick={handleDeleteTask}>작업 삭제</button>
                </div>
            </div>
        </div>
    );
}

export default TaskPage;
