import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit } from '@fortawesome/free-solid-svg-icons';

function MapManagementPage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null);
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [mapImage, setMapImage] = useState(null);
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false); // 팝업 상태

  useEffect(() => {
    fetchMaps();
  }, []);

  const fetchMaps = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.26:5557/map/maps', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
    }
  };

  const handleSelectMap = async (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);

    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.26:5557/map/file/${map._id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl);
    } catch (error) {
      console.error('Error fetching map image:', error);
    }
  };

  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    const confirmDelete = window.confirm('Are you sure you want to delete this map?');
    if (!confirmDelete) return;

    try {
      await axios.put(`http://172.30.1.26:5557/map/delete/${mapId}`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Map deleted successfully');
      fetchMaps();
      if (selectedMap && selectedMap._id === mapId) {
        setSelectedMap(null); // 선택된 맵을 삭제한 경우 초기화
      }
    } catch (error) {
      console.error('Error deleting map:', error);
      alert('Error deleting map');
    }
  };

  const handleUpdateMap = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.put(`http://172.30.1.26:5557/map/update/${selectedMap._id}`, {
        name: editName,
        description: editDescription,
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Map updated successfully');
      setIsEditPopupOpen(false); // 수정 후 팝업 닫기
      fetchMaps();
    } catch (error) {
      console.error('Error updating map:', error);
    }
  };

  const openEditPopup = (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);
    setIsEditPopupOpen(true); // 팝업 열기
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', height: '100vh' }}>
      {/* 상단 네비게이션 바 */}
      <header style={{ display: 'flex', justifyContent: 'space-between', padding: '20px', borderBottom: '1px solid #ccc' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      <div style={{ display: 'flex', height: 'calc(100% - 100px)', padding: '20px' }}>
        {/* Left side: Map List */}
        <div style={{ flex: '1', borderRight: '1px solid #ccc', paddingRight: '20px' }}>
          <h2 style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            Map List
            {/* 기존의 업로드 버튼 생략 */}
          </h2>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {maps.map((map) => (
              <li
                key={map._id}
                style={{ padding: '10px', cursor: 'pointer', borderBottom: '1px solid #ccc', display: 'flex', justifyContent: 'space-between' }}
              >
                <span onClick={() => handleSelectMap(map)} style={{ flexGrow: 1 }}>{map.name}</span>
                <button
                  onClick={() => openEditPopup(map)} // 연필 모양 버튼 클릭 시 팝업 열기
                  style={{
                    backgroundColor: 'transparent',
                    border: 'none',
                    cursor: 'pointer',
                    fontSize: '16px',
                    marginRight: '10px'
                  }}
                >
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button
                  onClick={() => handleDeleteMap(map._id)}
                  style={{
                    backgroundColor: 'transparent',
                    border: 'none',
                    color: 'red',
                    cursor: 'pointer',
                    fontSize: '16px'
                  }}
                >
                  &times;
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Center: Map Image */}
        <div style={{ flex: '2', padding: '20px', textAlign: 'center' }}>
          <h2>Selected Map</h2>
          {mapImage ? (
            <img src={mapImage} alt="Selected Map" style={{ maxWidth: '100%', height: 'auto' }} />
          ) : (
            <p>Select a map to view its image</p>
          )}
        </div>
      </div>

      {/* Edit Map Popup */}
      {isEditPopupOpen && (
        <div style={{
          position: 'fixed',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          backgroundColor: 'white',
          padding: '20px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          zIndex: 1000,
          width: '400px'
        }}>
          <h3>Edit Map</h3>
          <form onSubmit={handleUpdateMap}>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              placeholder="Map Name"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%' }}
            />
            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              placeholder="Map Description"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%' }}
            />
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
                Update
              </button>
              <button
                type="button"
                onClick={() => setIsEditPopupOpen(false)}
                style={{ padding: '10px', backgroundColor: 'red', color: 'white', border: 'none', cursor: 'pointer' }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default MapManagementPage;
