import React, { useEffect } from 'react';
import ROSLIB from 'roslib';
import './TeleopControl.css';

function TeleopControl({ onClose }) {
  // ROS와 연결 설정
  const ros = new ROSLIB.Ros({
    url: 'ws://localhost:9090', // rosbridge WebSocket 주소
  });

  // cmd_vel 토픽 설정
  const cmdVel = new ROSLIB.Topic({
    ros: ros,
    name: '/cmd_vel',
    messageType: 'geometry_msgs/Twist',
  });

  // 로봇에 명령을 보내는 함수
  const sendCommand = (linear, angular) => {
    const twist = new ROSLIB.Message({
      linear: { x: linear, y: 0.0, z: 0.0 },
      angular: { x: 0.0, y: 0.0, z: angular },
    });
    cmdVel.publish(twist);
  };

  // 버튼 꾹 누르기 처리를 위한 변수
  let commandInterval;

  // 버튼을 누를 때 반복해서 명령을 보내는 함수
  const handleButtonPress = (linear, angular) => {
    sendCommand(linear, angular);
    commandInterval = setInterval(() => {
      sendCommand(linear, angular);
    }, 100); // 100ms마다 명령을 반복 전송
  };

  // 버튼에서 손을 뗄 때 반복을 멈추는 함수
  const handleButtonRelease = () => {
    clearInterval(commandInterval);
    sendCommand(0, 0); // 버튼에서 손을 떼면 정지 명령 전송
  };

  return (
    <div className="popup-overlay">
      <div className="popup">
        <h3>Teleop Control</h3>
        <div className="teleop-controls">
          <div className="control-grid">
            {/* 위쪽 버튼 */}
            <button
              onMouseDown={() => handleButtonPress(0.5, 0)}
              onMouseUp={handleButtonRelease}
              onMouseLeave={handleButtonRelease}
              className="control-btn up-btn"
            >
              ↑
            </button>
            {/* 왼쪽 버튼 */}
            <button
              onMouseDown={() => handleButtonPress(0, 0.5)}
              onMouseUp={handleButtonRelease}
              onMouseLeave={handleButtonRelease}
              className="control-btn left-btn"
            >
              ←
            </button>
            {/* 정지 버튼 */}
            <button
              onClick={() => sendCommand(0, 0)}
              className="control-btn stop-btn"
            >
              정지
            </button>
            {/* 오른쪽 버튼 */}
            <button
              onMouseDown={() => handleButtonPress(0, -0.5)}
              onMouseUp={handleButtonRelease}
              onMouseLeave={handleButtonRelease}
              className="control-btn right-btn"
            >
              →
            </button>
            {/* 아래쪽 버튼 */}
            <button
              onMouseDown={() => handleButtonPress(-0.5, 0)}
              onMouseUp={handleButtonRelease}
              onMouseLeave={handleButtonRelease}
              className="control-btn down-btn"
            >
              ↓
            </button>
          </div>
        </div>
        <button onClick={onClose} className="close-btn">
          Close
        </button>
      </div>
    </div>
  );
}

export default TeleopControl;
