// worldToMap 함수 수정
function worldToMap(x, y, naturalWidth, naturalHeight) {
  const mapX = (x / worldWidth) * naturalWidth; // 월드 좌표를 이미지 자연 크기로 변환
  const mapY = (y / worldHeight) * naturalHeight;
  return { x: mapX, y: mapY };
}

// 지도 이미지 로드 시 자연 크기 저장
const handleImageLoad = (e) => {
  const img = e.target;
  setImageNaturalWidth(img.naturalWidth);
  setImageNaturalHeight(img.naturalHeight);
  console.log(`Image Loaded: Natural (${img.naturalWidth}, ${img.naturalHeight})`);
};

// 노드 렌더링 시 자연 크기를 기준으로 계산
{nodes.map((node) => {
  if (!imageNaturalWidth || !imageNaturalHeight) return null;

  const { x, y } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight); // 자연 크기로 변환
  return (
    <div
      key={node._id}
      className="node"
      style={{
        position: 'absolute',
        top: `${y}px`,
        left: `${x}px`,
        width: '10px',
        height: '10px',
        backgroundColor: 'red',
        borderRadius: '50%',
        transform: 'translate(-50%, -50%)',
      }}
      title={node.name}
    />
  );
})}
