import React, { useEffect, useRef } from 'react';

function MapComponent({ robots, mapUrl }) {
  const canvasRef = useRef(null);
  const mapImageRef = useRef(null);

  const mapResolution = 0.05; // 해상도 값 (픽셀당 미터)
  const mapOrigin = [-10.0, -10.0]; // 원점 좌표 (x, y)

  // 팝업을 띄우는 함수
  const openControlPopup = () => {
    window.open('/robotControlPopup', 'Robot Control', 'width=400,height=400');
  };

  // 지도 이미지를 로드하고 그리는 함수
  const loadMapImage = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    mapImageRef.current = new Image();
    mapImageRef.current.src = mapUrl;

    mapImageRef.current.onload = () => {
      // 컨테이너 크기와 이미지 비율에 맞춰 캔버스 크기 설정
      const containerWidth = canvas.parentElement.clientWidth;
      const containerHeight = canvas.parentElement.clientHeight;
      const imgWidth = mapImageRef.current.width;
      const imgHeight = mapImageRef.current.height;
      const imgAspectRatio = imgWidth / imgHeight;
      const containerAspectRatio = containerWidth / containerHeight;

      let drawWidth, drawHeight;

      // 이미지 비율에 따라 캔버스에 맞게 크기를 조정
      if (imgAspectRatio > containerAspectRatio) {
        drawWidth = containerWidth;
        drawHeight = containerWidth / imgAspectRatio;
      } else {
        drawHeight = containerHeight;
        drawWidth = containerHeight * imgAspectRatio;
      }

      canvas.width = drawWidth;
      canvas.height = drawHeight;

      // 지도 이미지를 그립니다.
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(mapImageRef.current, 0, 0, drawWidth, drawHeight);

      // 초기 로봇 위치를 그립니다.
      drawRobots();
    };

    mapImageRef.current.onerror = (err) => {
      console.error('지도 이미지를 로드하는 중 오류 발생:', err);
    };
  };

  // 로봇들을 그리는 함수
  const drawRobots = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!mapImageRef.current) return;

    // 캔버스를 지우고 지도 이미지를 다시 그립니다.
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.drawImage(mapImageRef.current, 0, 0, canvas.width, canvas.height);

    // 로봇들을 지도 위에 그립니다.
    robots.forEach((robot) => {
      const { x, y } = robot.location;
      const { mapX, mapY } = worldToMap(x, y);

      let robotColor = 'gray';
      if (robot.status?.state === 'Tasking') {
        robotColor = 'blue';
      } else if (robot.status?.state === 'Waiting') {
        robotColor = 'yellow';
      } else if (robot.status?.state === 'Error') {
        robotColor = 'red';
      }

      ctx.fillStyle = robotColor;
      ctx.beginPath();
      ctx.arc(mapX, mapY, 5, 0, 2 * Math.PI);
      ctx.fill();
    });
  };

  // 월드 좌표를 맵 좌표로 변환하는 함수 (해상도와 원점 고려)
  const worldToMap = (x, y) => {
    const mapImage = mapImageRef.current;
    const mapWidth = mapImage.width;
    const mapHeight = mapImage.height;

    // 월드 좌표를 맵 좌표로 변환 (원점과 해상도 적용)
    const mapX = (x - mapOrigin[0]) / mapResolution;
    const mapY = mapHeight - (y - mapOrigin[1]) / mapResolution; // Y 좌표는 캔버스 상하 반전 처리
    return { mapX, mapY };
  };

  // 지도 이미지는 한 번만 로드
  useEffect(() => {
    if (mapUrl) {
      loadMapImage();
    }
  }, [mapUrl]);

  // 로봇 위치가 변경될 때마다 업데이트
  useEffect(() => {
    if (mapImageRef.current) {
      drawRobots();
    }
  }, [robots]);

  return (
    <div className="map-canvas-container">
      {mapUrl ? (
        <>
          <canvas ref={canvasRef} className="canvas"></canvas>
          <button onClick={openControlPopup} style={{ marginTop: '10px', padding: '10px 20px', fontSize: '16px' }}>
            로봇 조종
          </button>
        </>
      ) : (
        <p>지도를 불러오는 중...</p>
      )}
    </div>
  );
}

export default MapComponent;
