import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

function TestPage() {
  const [mapUrl, setMapUrl] = useState(null);
  const [robots, setRobots] = useState([]);
  const canvasRef = useRef(null);
  const [mapResolution, setMapResolution] = useState(0.05); // 기본 값, 실제 파일에서 가져올 예정
  const [mapDimensions, setMapDimensions] = useState({ width: 0, height: 0 });

  useEffect(() => {
    fetchMonitoredMap();
    fetchMapMetadata();
    fetchRobots(); // 로봇 리스트 API 호출
  }, []);

  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      window.location.href = '/login'; // Redirect to login if token is missing
      return null;
    }
    return token;
  };

  const fetchMonitoredMap = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/monitored/file', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = URL.createObjectURL(response.data);
      setMapUrl(url);

      const img = new Image();
      img.src = url;
      img.onload = () => {
        setMapDimensions({ width: img.width, height: img.height });
      };
    } catch (error) {
      console.error('Error fetching monitored map:', error);
    }
  };

  const fetchMapMetadata = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/monitored/metadata', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const metadata = response.data;
      const resolution = parseFloat(metadata.match(/resolution:\s*(\d+.\d+)/)[1]);
      setMapResolution(resolution);
    } catch (error) {
      console.error('Error fetching map metadata:', error);
    }
  };

  // 주어진 fetchRobots 함수 사용
  const fetchRobots = async () => {
    console.log("Fetching robots");
    const token = getToken();
    if (!token) {
      console.log("No token found, exiting fetchRobots");
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:5559/robot/robots', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      setRobots(response.data);
    } catch (error) {
      console.error('Error fetching robots:', error);
    }
  };

  const worldToMap = (x, y, mapWidth, mapHeight, resolution) => {
    const mapX = (x + mapWidth * resolution / 2) / resolution;
    const mapY = mapHeight - (y + mapHeight * resolution / 2) / resolution;
    return { mapX, mapY };
  };

  const drawMapAndRobots = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const mapImage = new Image();
    mapImage.src = mapUrl;

    mapImage.onload = () => {
      canvas.width = mapImage.width;
      canvas.height = mapImage.height;
      ctx.drawImage(mapImage, 0, 0);

      robots.forEach((robot) => {
        const { x, y } = robot.location; // 로봇 위치 정보 사용
        const { mapX, mapY } = worldToMap(x, y, mapImage.width, mapImage.height, mapResolution);
        ctx.fillStyle = 'red';
        ctx.beginPath();
        ctx.arc(mapX, mapY, 5, 0, 2 * Math.PI);
        ctx.fill();
      });
    };
  };

  useEffect(() => {
    if (mapUrl) {
      drawMapAndRobots();
    }
  }, [mapUrl, robots]);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Monitoring Page</h2>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
        {/* Left side - Map canvas */}
        <div style={{ flex: '3', display: 'flex', justifyContent: 'center', alignItems: 'center', marginRight: '20px' }}>
          {mapUrl ? (
            <canvas ref={canvasRef} style={{ border: '1px solid #ccc' }}></canvas>
          ) : (
            <p>Loading map...</p>
          )}
        </div>

        {/* Right side - Robot list */}
        <div style={{ flex: '1', padding: '20px', borderLeft: '1px solid #ccc', overflowY: 'auto', maxHeight: '600px' }}>
          <h3>Robot List</h3>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {robots.length > 0 ? (
              robots.map((robot) => (
                <li key={robot._id} style={{ marginBottom: '10px', borderBottom: '1px solid #ddd', padding: '10px' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <span><strong>State:</strong> {robot.status.state}</span>
                    <span><strong>Battery:</strong> {robot.status.battery}%</span>
                    <span><strong>IP:</strong> {robot.ip}</span>
                  </div>
                </li>
              ))
            ) : (
              <p>No robots connected.</p>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default TestPage;
