import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import CreateTask from './CreateTask';
import TaskFlow from './TaskFlow';
import './TaskPage.css';

function TaskPage() {
    const [tasks, setTasks] = useState([]);
    const [selectedTask, setSelectedTask] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [monitoredMap, setMonitoredMap] = useState(null);
    const [temporaryWorkflow, setTemporaryWorkflow] = useState([]);
    const [deletedWorkflowNodes, setDeletedWorkflowNodes] = useState([]);

    const [taskName, setTaskName] = useState('');
    const [taskDescription, setTaskDescription] = useState('');
    const [taskType, setTaskType] = useState('A');
    const token = localStorage.getItem('token');

    useEffect(() => {
        fetchTasks();
    }, [token]);

    const fetchTasks = async () => {
        try {
            const mapResponse = await axios.get('http://172.30.1.30:5557/map/monitored', {
                headers: { Authorization: `Bearer ${token}` },
            });

            const monitoredMapData = mapResponse.data;
            setMonitoredMap(monitoredMapData);

            if (!monitoredMapData || !monitoredMapData._id) {
                console.error('모니터링 중인 맵 정보가 없습니다.');
                return;
            }

            const taskResponse = await axios.get(`http://172.30.1.30:8080/task/tasks?mapId=${monitoredMapData._id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            const taskData = taskResponse.data;
            setTasks(taskData);

            if (taskData.length > 0) handleTaskClick(taskData[0]);
        } catch (error) {
            console.error('작업 리스트를 가져오는 중 오류가 발생했습니다.', error);
        }
    };

    const handleTaskClick = (task) => {
        setSelectedTask(task);
        setTaskName(task.name);
        setTaskDescription(task.description || '');
        setTaskType(task.taskType || 'A');
        setTemporaryWorkflow([]);
        setDeletedWorkflowNodes([]);
    };

    const togglePopup = () => setIsPopupOpen((prev) => !prev);

    const handleTaskCreated = (newTask) => {
        setTasks((prevTasks) => [...prevTasks, newTask]);
        handleTaskClick(newTask);
    };

    const handleSaveTask = async () => {
        if (!selectedTask) {
            alert('저장할 작업을 선택하세요.');
            return;
        }

        try {
            const updatedTask = {
                ...selectedTask,
                workflow: [...selectedTask.workflow, ...temporaryWorkflow],
                name: taskName,
                description: taskDescription,
                taskType: taskType,
            };

            // 선택된 작업을 업데이트
            await axios.put(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}`, updatedTask, {
                headers: { Authorization: `Bearer ${token}` },
            });

            // 삭제된 워크플로우 노드 중 _id가 있는 항목만 DB에 업데이트
            for (const { taskId, nodeId } of deletedWorkflowNodes.filter(node => node.nodeId)) {
                await axios.put(
                    `http://172.30.1.30:8080/task/tasks/${taskId}/workflow/${nodeId}/nodeToZero`,
                    null,
                    { headers: { Authorization: `Bearer ${token}` }
                    });
            }

            // 상태 초기화 및 페이지 새로 고침
            setTemporaryWorkflow([]);
            setDeletedWorkflowNodes([]);
            window.location.reload();
        } catch (error) {
            console.error('작업 저장 중 오류가 발생했습니다.', error);
            alert('작업 저장 중 오류가 발생했습니다.');
        }
    };

    // 임시 워크플로우 항목을 추가하는 함수
    const handleTemporaryFlowAdd = (newFlow) => {
        setTemporaryWorkflow((prev) => [...prev, newFlow]);
    };

    const handleTemporaryFlowDelete = (taskId, flowId) => {
        if (!flowId) {
            // 임시 워크플로우에 있는 항목 삭제
            setTemporaryWorkflow((prev) => prev.filter((item) => item.node !== taskId));
        } else {
            // 삭제된 워크플로우 노드로 추가
            setDeletedWorkflowNodes((prev) => [...prev, { taskId, nodeId: flowId }]);
        }
    };

    return (
        <div className="page-container">
            <header className="header">
                <UserInfo />
                <LogoutButton />
            </header>
            <Navbar className="navbar" />
            <div className="main-content">
                <div className="task-list">
                    <div className="task-list-header">
                        <h3>작업 리스트</h3>
                        {monitoredMap && (
                            <span style={{ color: 'gray', marginLeft: '10px', fontSize: '14px' }}>
                                - {monitoredMap.name}
                            </span>
                        )}
                        <button className="add-task-btn" onClick={togglePopup}>+</button>
                    </div>
                    <ul>
                        {tasks.map((task) => (
                            <li
                                key={task._id}
                                onClick={() => handleTaskClick(task)}
                                className={selectedTask && selectedTask._id === task._id ? 'selected' : ''}
                            >
                                {task.name}
                            </li>
                        ))}
                    </ul>
                </div>

                <div className="task-details">
                    {selectedTask ? (
                        <div>
                            <h3>작업 상세 정보</h3>
                            <label>
                                <strong>작업 이름:</strong>
                                <input
                                    type="text"
                                    className="input-field"
                                    value={taskName}
                                    onChange={(e) => setTaskName(e.target.value)}
                                />
                            </label>
                            <br />
                            <label>
                                <strong>작업 설명:</strong>
                                <textarea
                                    className="textarea-field"
                                    value={taskDescription}
                                    onChange={(e) => setTaskDescription(e.target.value)}
                                />
                            </label>
                            <br />
                            <div className="task-row">
                                <label>
                                    <strong>작업 유형:</strong>
                                    <select
                                        className="select-field"
                                        value={taskType}
                                        onChange={(e) => setTaskType(e.target.value)}
                                    >
                                        <option value="A">A</option>
                                        <option value="B">B</option>
                                        <option value="C">C</option>
                                    </select>
                                </label>
                                <label className="created-date">
                                    <strong>생성일:</strong> {new Date(selectedTask.createdAt).toLocaleDateString()}
                                </label>
                            </div>

                            <h4>작업 플로우</h4>
                            <div className="task-flow-section">
                                <TaskFlow
                                    taskId={selectedTask._id}
                                    workflow={selectedTask.workflow}
                                    onTemporaryFlowAdd={handleTemporaryFlowAdd}
                                    onTemporaryFlowDelete={handleTemporaryFlowDelete}
                                />
                            </div>
                        </div>
                    ) : (
                        <p>작업을 선택하세요.</p>
                    )}
                </div>

                {isPopupOpen && (
                    <CreateTask onClose={togglePopup} onTaskCreated={handleTaskCreated} />
                )}

                <div className="floating-buttons">
                    <button className="assign-task-btn" onClick={() => alert('작업 할당')}>작업 할당</button>
                    <button className="save-task-btn" onClick={handleSaveTask}>작업 저장</button>
                    <button className="delete-task-btn" onClick={() => alert('작업 삭제')}>작업 삭제</button>
                </div>
            </div>
        </div>
    );
}

export default TaskPage;
